mapper.select:

select 

from historyorder
where historyorderid = #{historyorderid,jdbcType=VARCHAR}
  and historyproductid = #{historyproductid,jdbcType=VARCHAR}

mapper.delete:

delete from historyorder
where historyorderid = #{historyorderid,jdbcType=VARCHAR}
  and historyproductid = #{historyproductid,jdbcType=VARCHAR}
